import React, { useState, useEffect } from 'react';
import axios from 'axios';

interface PokemonInfo {
    id: number;
    name: string;
    height: number;
    weight: number;
    types: string[];
    description: string;
    flavorText: string;
    image: string;
}

const Pokemon: React.FC = () => {
    const [pokemonName, setPokemonName] = useState<string>('');
    const [pokemonData, setPokemonData] = useState<PokemonInfo | null>(null);
    const [loading, setLoading] = useState<boolean>(false);
    const [error, setError] = useState<string | null>(null);

  // ポケモン情報をAPIから取得する関数
  const fetchPokemonData = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get<PokemonInfo>(`http://localhost:5001/api/pokemon/${pokemonName}`);
      setPokemonData(response.data);
    } catch (error) {
      setError('ポケモンのデータを取得できませんでした。');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
    <h1>ポケモン情報</h1>
    <input
      type="text"
      value={pokemonName}
      onChange={(e) => setPokemonName(e.target.value)}
      placeholder="ポケモンの名前を入力"
    />
    <button onClick={fetchPokemonData}>検索</button>

    {loading && <p>読み込み中...</p>}
    {error && <p style={{ color: 'red' }}>{error}</p>}

    {pokemonData && (
      <div>
        <h2>{pokemonData.name}</h2>
        <img src={pokemonData.image} alt={pokemonData.name} />
        <p>番号: {pokemonData.id}</p>
        <p>身長: {pokemonData.height} kg</p>
        <p>体重: {pokemonData.weight} cm</p>
        <p>タイプ: {pokemonData.types.join(', ')}</p>
        <p>説明: {pokemonData.description}</p>
      </div>
    )}
  </div>
);
}

export default Pokemon;
