import React, { useState } from 'react';
import axios from 'axios';
import styles from '../style/style.module.css'; // CSSモジュールのインポート

interface PokemonInfo {
  id: number;
  name: string;
  height: number;
  weight: number;
  types: string[];
  flavorText: string;
  image: string;
}

const App: React.FC = () => {
  const [pokemonName, setPokemonName] = useState<string>('');
  const [pokemonData, setPokemonData] = useState<PokemonInfo | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  // ポケモン名（日本語）を英語名に変換する関数
  const getPokemonEnglishName = async (japaneseName: string): Promise<string> => {
    try {
      // ポケモンのデータを取得する
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon-species/${japaneseName.toLowerCase()}`);
      const speciesData = response.data;

      // 英語名を取得する
      const englishName = pokemonData.name; // 英語名（APIで返される名前は英語名）
      return englishName || japaneseName; // 英語名が見つからない場合は日本語名を使用
    } catch (error) {
      console.error('英語名の取得に失敗しました:', error);
      return japaneseName; // エラーハンドリング
    }
  };

  // ポケモンデータを取得する関数
  const fetchPokemonData = async () => {
    setLoading(true);
    setError(null);

    try {
      const englishName = await getPokemonEnglishName(pokemonName);

      const response = await axios.get<PokemonInfo>(`http://localhost:5001/api/pokemon/${englishName}`);
      setPokemonData(response.data);
    } catch (error) {
      setError('ポケモンのデータを取得できませんでした。');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>ポケモン情報</h1>
      <input
        className={styles.inputField}
        type="text"
        value={pokemonName}
        onChange={(e) => setPokemonName(e.target.value)}
        placeholder="ポケモンの名前を入力"
      />
      <button className={styles.button} onClick={fetchPokemonData}>検索</button>

      {loading && <p>読み込み中...</p>}
      {error && <p className={styles.error}>{error}</p>}

      {pokemonData && (
        <div className={styles.pokemonCard}>
          <h2>{pokemonData.name}</h2>
          <img src={pokemonData.image} alt={pokemonData.name} />
          <p>番号: {pokemonData.id}</p>
          <p>身長: {pokemonData.height} decimetres</p>
          <p>体重: {pokemonData.weight} hectograms</p>

          <div className={styles.types}>
            {pokemonData.types.map((type, index) => (
              <span key={index} className={styles.type}>{type}</span>
            ))}
          </div>

          <p>説明: {pokemonData.flavorText}</p>
        </div>
      )}
    </div>
  );
};

export default App;
